(function() {
     $(document).ready(function() {
        $(".community__slider .slider__slides").slick({
            prevArrow: '<a class="slider__prev slider__prev--community"></a>',
            nextArrow: '<a class="slider__next slider__next--community"></a>',
            appendArrows: $(".community__slider .slider__arrows"),
            slidesToShow: 2,
            slidesToScroll: 2,
            responsive: [
                {
                    breakpoint: 1075,
                    settings: {
                        slidesToScroll: 1,
                        variableWidth: true
                    }
                },

                {
                    breakpoint: 780,
                    settings: {
                        slidesToShow: 1,
                        slidesToScroll: 1
                    }
                }
            ]
        });
    }); 
})();
(function() {
        $(".design__slider .slider__slides").slick({
            appendArrows: $(".design .slider__arrows"),
            prevArrow: '<a class="slider__prev slider__prev--design"></a>',
            nextArrow: '<a class="slider__next slider__next--design"></a>'
        }); 
})();
(function() {
    $(document).ready(function() {
        var flag = false,
            menu = $(".mobile-nav");

        function toggleMenu() {
            $(".mobile-menu-icon__lines").toggleClass("mobile-menu-icon__lines--close");
            menu.toggleClass("banner__mobile-nav--show");
        }

        $(".mobile-menu-icon").click(toggleMenu);

        $(document).click(function(e) {
            var target = $(e.target);

            if (target.hasClass("mobile-nav") || target.parent().hasClass("mobile-nav")) return;
            
            if (!target.hasClass("mobile-menu-icon") && !target.parent().hasClass("mobile-menu-icon") && menu.hasClass("banner__mobile-nav--show")) {
                toggleMenu();
            }
        });
    });
})();
(function() {
    $(document).ready(function() {
        $(".slider__filter-icon").magnificPopup({
            type: "image",
            gallery: {
                enabled: true
            }
        })
    });
})();
(function() {
    $('.nav__link').on('click', function(e){
        e.preventDefault();
        if ($($(this).attr('href')))
            {
                $('html,body').animate({
                    scrollTop: $($(this).attr('href')).offset().top
            }, 500);
            } else {
                return false;
            }  
    });

    $('.mobile-nav__link').on('click', function(e){
        e.preventDefault();
        if ($($(this).attr('href')))
            {
                $('html,body').animate({
                    scrollTop: $($(this).attr('href')).offset().top
            }, 500);

            $('.banner__mobile-nav').removeClass('banner__mobile-nav--show');
            $(".mobile-menu-icon__lines").removeClass("mobile-menu-icon__lines--close");
            } else {
                return false;
            }  
    });
})();